mapper.select:
select ord.order_ID as orderId,ord.Class_date as classDate,ord.order_class_bh as orderClassBh,ord.order_number as orderNumber,ord.container_type as containerType,
ord.container_boxAmount as containerBoxamount,ord.order_unloadSite as orderUnloadsite,goods.goods_name as goodsName
from busi_shippingorder ord
left join busi_shippingorder_goods goods on goods.order_ID = ord.order_ID

    ord.line_typeid = '2' and ord.client_ID = #{clientId}
     and ord.order_number like concat('%', #{orderNumber}, '%')
     and ord.order_unloadSite like concat('%', #{orderUnloadsite}, '%')
     and ord.Class_date >= #{classDateStart,jdbcType=DATE}
     and ord.Class_date <= #{classDateEnd,jdbcType=DATE}

order by ord.tj_time desc

